import Header from "./components/Header";
import Form from "./components/Generalform";
import Educationform from "./components/Educationform";
import Workform from "./components/Workform";

import { useState,useEffect } from "react";
import PrintButton from "./components/PrintButton";
import ToggleFormButton from "./components/Button";








const  App = () =>    {
	const[printmode, setPrintmode] = useState(false)

	useEffect(() => {
		const handleBeforePrint = () => {
		  setPrintmode(true);
		};

		const handleAfterPrint = () => {
			setPrintmode(false);
		  };

		  window.addEventListener("beforeprint", handleBeforePrint);
		  window.addEventListener("afterprint", handleAfterPrint);

		  return () => {
			window.removeEventListener("beforeprint", handleBeforePrint);
			window.removeEventListener("afterprint", handleAfterPrint);
		  };
		}, []);





  const handlePrint = () => {

    window.print();
	setPrintmode(true);
  };



	return(
<div className='container'>
	<Header />
	<Form /> 

	{!printmode &&   <ToggleFormButton formComponent={<Educationform />} buttonText=" ADD EDUCATION  " /> }



	{!printmode && <ToggleFormButton formComponent={<Workform />} buttonText=" ADD WORK EXPERIENCE" /> }


	{!printmode && <PrintButton onClick={handlePrint} />}








</div>



	);

};





export default App ;
