import React from 'react';
import logo from "../images/logo.png"; 
import Product from './Product';
import { CartContext } from './Cartcontext';
import { cards, getProductQuantity } from './Productlist';
import { useContext } from 'react';


const Header = () => {
  const cart = useContext(CartContext);
  return (
    <header>
      <nav className="navbar">
        <a href="/" className="logo">
          <img src={logo} alt="logo" />
        </a>

        <ul className="nav-links">
          <li className="nav-item"><a href="#">Home</a></li>
          <li className="nav-item"><a href="#">Product</a></li>
          <li className="nav-item"><a href="/checkout">Cart </a></li>

          <button className="shopping-cart-button" style={{ fontSize: "26px" }}>({cart.items.length})
  <i className="fa fa-shopping-cart"></i>
</button>


        </ul>
      </nav>


    </header>
  );
}

export default Header;
