import React from 'react';
import Header from './components/Header';
import Prepage from './components/Prepage';
import Product from './components/Product';
import Checkout from './components/Checkout';
import { useState } from 'react';

const App = () => {
  const [show, setShow] = useState(true);
  const [cart, setCart] = useState([]);
  const [warning, setWarning] = useState(false);

  const handleclick = (item) => {
    let isPresent = false;
    cart.forEach((product) => {
      if (item.id === product.id) {
        isPresent = true;
      }
    });

    if (isPresent) {
      setWarning(true);
      setTimeout(() => {
        setWarning(false);
      }, 2000);
      return;
    }

    setCart([...cart, item]);
  };
console.log(warning)

const handleChange = (card)=>{
console.log(card)
}

  return (
    <>
      <Header size={cart.length} setShow={setShow} />
      <Prepage /> 
     {show ? <Product size={cart.length} handleclick={handleclick} />   :  <Checkout cart={cart} setCart={setCart} handleChange={handleChange}    />}
      {warning && <div className="warning">Item already in cart</div>}

    </>
  );
};

export default App;
