import { useState } from "react";

const Workform = () => {
    const [companyName, setCompanyName] = useState('');
    const [positionTitle, setPositionTitle] = useState('');
    const [fromDate, setFromDate] = useState('');
    const [toDate, setToDate] = useState('');
    const [description, setDescription] = useState('');
    const [submittedData, setSubmittedData] = useState(null);



      const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission, e.g., send data to the server
        const formData = {
            companyName,
            positionTitle,
            fromDate,
            toDate,
            description
          };



      setSubmittedData(formData)



        };



      return (
    <div>

    <h3> Work Experience   </h3>
      <form className="add-form"      onSubmit={handleSubmit} >
  <div className="form-row">
    <div className="form-control">
      <label>Company Name</label> 
      <input type="text" placeholder="Enter Company Name"   

      name="companyName"
      value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
      />
    </div>

    <div className="form-control">
      <label>Position Title</label> 
      <input type="text" placeholder="Enter Position Title" 

      value={positionTitle}
      onChange={(e) => setPositionTitle(e.target.value)}


      />
    </div>
  </div>

  <div className="form-row">
    <div className="form-control">
      <label>From</label> 
      <input type="date"       
       value={fromDate}
       onChange={(e) => setFromDate(e.target.value)}


      />
    </div>

    <div className="form-control">
      <label>To</label> 
      <input type="date" 

      value={toDate}
      onChange={(e) => setToDate(e.target.value)}

      />
    </div>
  </div>

  <div className="form-control">
    <label>Description Of Your Job</label>
    <textarea placeholder="Main activities and Others"   

    value={description}
    onChange={(e) => setDescription(e.target.value)}

    ></textarea>
  </div>

  <input type="submit" value="Save" className="btn btn-block" />
</form>



{submittedData && (
        <div className="submitted-data  show-on-print  ">

          <h3>Work Experience</h3>
          <p>Company Name: {submittedData.companyName}</p>
          <p>Position Title: {submittedData.positionTitle}</p>
          <p>From: {submittedData.fromDate}</p>
          <p>To: {submittedData.toDate}</p>
          <p>Description: {submittedData.description}</p>
        </div>
      )} 







    </div>
  )
}

export default Workform
