import { useState } from 'react';

const Educationform = () => {
  const [schoolName, setSchoolName] = useState('');
  const [titleOfStudy, setTitleOfStudy] = useState('');
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [submittedData, setSubmittedData] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to the server
    const formData = {
      schoolName,
      titleOfStudy,
      fromDate,
      toDate,
    };



setSubmittedData(formData)



  };

  return (
    <div>
      <h3>Education</h3>









      <form className="add-form" onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-control">
            <label>School Name</label>
            <input
              type="text"
              placeholder="Enter School Name"
              value={schoolName}
              onChange={(e) => setSchoolName(e.target.value)}
            />
          </div>

          <div className="form-control">
            <label>Title of Study</label>
            <input
              type="text"
              placeholder="Enter Title of Study"
              value={titleOfStudy}
              onChange={(e) => setTitleOfStudy(e.target.value)}
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-control">
            <label>From</label>
            <input
              type="date"
              value={fromDate}
              onChange={(e) => setFromDate(e.target.value)}
            />
          </div>

          <div className="form-control">
            <label>To</label>
            <input
              type="date"
              value={toDate}
              onChange={(e) => setToDate(e.target.value)}
            />
          </div>
        </div>

        <input type="submit" value="Save" className="btn btn-block" />
      </form>


      {submittedData && (
        <div className="submitted-data">
          <h3> Education </h3>
          <p>School Name: {submittedData.schoolName}</p>
          <p> Title: {submittedData.titleOfStudy}</p>
          <p>From: {submittedData.fromDate}</p>
          <p>To: {submittedData.toDate}</p>
        </div>
      )} 




    </div>
  );
};

export default Educationform;
