import React, { useContext } from 'react';
import img4 from '../images/img4.jpg';
import Header from './Header';
import { CartContext } from './Cartcontext';
import { cards, getProductQuantity } from './Productlist';

const Checkout = (probs) => {
  const carditem = probs.cards
  const cart = useContext(CartContext);
  const productquantity = cart.getProductQuantity(cards.id)


  return (
    <div>
      <Header />
      <div className="checkout">
        <div className="checkcard">
          <h3 className="heading">Your Shopping Cart</h3>


          {cards.map((card) => (
            <div key={card.id}>
              <img src={card.image} alt="Denim Jeans" />
              <h1>{card.title}</h1>
              <p className="price">${card.price}</p>
              <p>
                <div className="flexbutton">
                  <button className="counter" onClick={()=> cart.addOneCart(card.id)}      >+</button>
                  <h2 id="counting">  {productquantity}</h2>

                  <button className="counter"  onClick={()=> cart. removeOneCart(card.id)}>-</button>
                </div>
              </p>
            </div>
          ))}

          <p>
            <button className="pay">Proceed To Payment</button>
          </p>

          <div className="back">
            <a className="aclass" href="/index.html">
              Back To Shop
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
