import React, { createContext, useState, useEffect } from 'react';

const CartContext = createContext({
  items: [],
  getProductQuantity: () => {},
  addOneCart: () => {},
  removeOneCart: () => {},
  deleteFromCart: () => {},
  getTotalCost: () => {}
});

function CartProvider({ children }) {
  const [cartProducts, setCartProducts] = useState([]);

  // Load cart data from local storage on initial render
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartProducts(JSON.parse(savedCart));
    }
  }, []);

  // Save cart data to local storage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartProducts));
  }, [cartProducts]);

  function getProductQuantity(id) {
    const quantity = cartProducts.find((product) => product.id === id)?.quantity;
    if (quantity === undefined) {
      return 0;
    }
    return quantity;
  }

  function addOneCart(id) {
    const quantity = getProductQuantity(id);

    if (quantity === 0) {
      setCartProducts([...cartProducts, { id: id, quantity: 1 }]);
    } else {
      setCartProducts(
        cartProducts.map((product) =>
          product.id === id ? { ...product, quantity: product.quantity + 1 } : product
        )
      );
    }
  }

  function removeOneFromCart(id) {
    const quantity = getProductQuantity(id);

    if (quantity === 1) {
      deleteFromCart(id);
    } else {
      setCartProducts(
        cartProducts.map((product) =>
          product.id === id ? { ...product, quantity: product.quantity - 1 } : product
        )
      );
    }
  }

  function deleteFromCart(id) {
    setCartProducts(cartProducts.filter((currentProduct) => currentProduct.id !== id));
  }

  function getTotalCost() {
    return cartProducts.reduce((total, product) => total + product.quantity * product.price, 0);
  }

  const contextValue = {
    items: cartProducts,
    getProductQuantity,
    addOneCart,
    removeOneFromCart,
    deleteFromCart,
    getTotalCost
  };

  return <CartContext.Provider value={contextValue}>{children}</CartContext.Provider>;
}

export { CartContext, CartProvider };
