import React, { useContext } from 'react';
import { CartContext } from './CartContext';

const Checkout = () => {
  const cart = useContext(CartContext);

  return (
    <div>
      <h1>Checkout</h1>
      <ul>
        {cart.items.map((item) => (
          <li key={item.id}>
            {item.quantity} x {item.title}
          </li>
        ))}
      </ul>
      <p>Total Cost: ${cart.getTotalCost()}</p>
    </div>
  );
};

export default Checkout;
